template.value:
/**
     * 列表页
     *
     * @return array
     */
    public function actionIndex()
    {
        $model = CmsCategoryTypeService::find();

        $model = G::modelAndFilter($model, $this->filterAccess);

        $total = $model->count();
        $pager = new Pagination([
            'totalCount' => $total,
            'defaultPageSize' => G::getPageSize(),
            'pageParam' => 'page',
            'pageSizeParam' => 'pageSize',
        ]);

        $model = G::modelSort($model, ['list_order' => SORT_ASC, 'ctid' => SORT_DESC], $this->sortAccess);
        $model = $model->offset($pager->offset)->limit($pager->limit);

        $model = $model->all();

        $data = [];
        foreach ($model as $item) {
            $tmp = $item->attributes;
            $data[] = $tmp;
        }

        return $this->success('OK', ['total' => $total, 'data' => $data, 'get' => G::get('filter')]);
    }
